con:configuration.scriptText:
//check response time
assert messageExchange.timeTaken < 1000

//check for Endpoint

def endpoint = "http://webservices.oorsprong.org/websamples.countryinfo/CountryInfoService.wso"
 assert messageExchange.getEndpoint() == endpoint 
log.info messageExchange.getEndpoint() 


//check for header
assert messageExchange.responseHeaders["Content-Length"] != null

//check attachments
assert messageExchange.responseAttachments.length == 0

//validate response nodes
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requsetHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
def currencyName = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName")
assert currencyName == "Rupees"

//to get response
def resp = messageExchange.responseContentAsXml.toString()
log.info resp

//get json response
import groovy.json.JsonSlurper
def responseMessage = messageExchange.response.responseContent
def json = new JsonSlurper().parseText(responseMessage)

//assert node values
log.info json.name
assert json.capital.toString() != null
assert json.name.toString() == "[Taiwan]"

//to get the Test Step Name
testStepName = messageExchange.modelItem.testStep.name
log.info testStepName

 //to store the response as Xml string
xmlHold = messageExchange.responseContentAsXml.toString()
